CREATE TABLE uma_table (
    count INTEGER PRIMARY KEY,
    uma_id INTEGER,
    uma_name TEXT,
);
CREATE TABLE events (
    event_id INTEGER PRIMARY KEY,
    event_name TEXT,
    uma_id INTEGER,
    uma_name TEXT,
    category TEXT,
);
CREATE TABLE event_choices (
    choice_id INTEGER PRIMARY KEY,
    event_id INTEGER,
    choice_label TEXT,
    speed INTEGER,
    stamina INTEGER,
    strength INTEGER,
    willpower INTEGER,
    intelligence INTEGER,
    hp INTEGER,
    mood INTEGER,
    skill_point INTEGER,
    condition TEXT,
    is_random_value BOOLEAN,
    is_random_attribute BOOLEAN,
    is_random_block_training BOOLEAN
);

